DROP TABLE IF EXISTS `vendors`;
CREATE TABLE `vendors` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) DEFAULT NULL,
  `name` varchar(50) DEFAULT NULL COMMENT '商家名称',
  `name_en` varchar(50) DEFAULT NULL COMMENT '商家英文名称',
  `address` varchar(100) DEFAULT NULL COMMENT '商家地址',
  `address_en` varchar(100) DEFAULT NULL COMMENT '商家英文地址',
  `contacts` varchar(255) DEFAULT NULL COMMENT '联系人',
  `mobile` varchar(255) DEFAULT NULL COMMENT '联系电话',
  `email` varchar(255) DEFAULT NULL COMMENT '联系邮箱',
  `icon` varchar(200) DEFAULT NULL,
  `other` varchar(500) DEFAULT NULL,
  `contract_expires` datetime DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=77 DEFAULT CHARSET=utf8 COMMENT='商家表';

